mapper.select:
select 
    kequn,company
from
    af_cc_mdk_kequn
where
    company = #{company,jdbcType=VARCHAR}
        or (namemc = #{namemc,jdbcType=VARCHAR}
        and legalformmc = #{legalformmc,jdbcType=VARCHAR}
        and sitemc = #{sitemc,jdbcType=VARCHAR} and namemc is not null and legalformmc is not null and sitemc is not null)
        or (namemc = #{namemc,jdbcType=VARCHAR}
        and legalformmc = #{legalformmc,jdbcType=VARCHAR}
        and addinfomc = #{addinfomc,jdbcType=VARCHAR} and namemc is not null and legalformmc is not null and addinfomc is not null)
        or (namemc = #{namemc,jdbcType=VARCHAR} and sitemc = #{sitemc,jdbcType=VARCHAR}
        and addinfomc = #{addinfomc,jdbcType=VARCHAR} and namemc is not null and sitemc is not null and addinfomc is not null)
limit 1
